package demo;

public class ascii {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'a';
        int ascii = ch;
     
        int castAscii = (int) ch;

        System.out.println("The ASCII value of " + ch + " is: " + ascii);
        System.out.println("The ASCII value of " + ch + " is: " + castAscii);
    

	}

}
